import heapq

def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, c = map(int, input().split())
        edges.append((u, v, c))
        edges.append((v, u, c))

    graph = {}
    for u, v, c in edges:
        if u not in graph:
            graph[u] = []
        graph[u].append((v, c))

    pq = [(0, 1, -1)]  # (cost, node, prev_company)
    visited = set()

    while pq:
        cost, node, prev_company = heapq.heappop(pq)
        if (node, prev_company) in visited:
            continue
        visited.add((node, prev_company))

        if node == n:
            print(cost)
            return

        if node in graph:
            for neighbor, company in graph[node]:
                new_cost = cost
                if prev_company != -1 and prev_company != company:
                    new_cost += 1
                heapq.heappush(pq, (new_cost, neighbor, company))

    print(-1)

solve()
