n, m, k = map(int, input().split())
mod = 1000000007

dp = {}

def solve(a, b, c):
    if (a, b, c) in dp:
        return dp[(a, b, c)]
    if a == 0:
        return 1
    if b == 0:
        return 0
    if c == 0:
        return 0
    ans = 0
    ans = (ans + solve(a - 1, b, c)) % mod
    ans = (ans + solve(a, b - 1, c)) % mod
    ans = (ans + solve(a, b, c-1)) % mod
    dp[(a, b, c)] = ans
    return ans

ans = 0
for i in range(3**n):
    a_deck = []
    temp = i
    for j in range(n):
        a_deck.append(temp % 3)
        temp //= 3

    for j in range(3**m):
        b_deck = []
        temp = j
        for l in range(m):
            b_deck.append(temp % 3)
            temp //= 3

        for l in range(3**k):
            c_deck = []
            temp = l
            for p in range(k):
                c_deck.append(temp % 3)
                temp //= 3

            cur_a = n
            cur_b = m
            cur_c = k
            turn = 0
            while cur_a > 0 or cur_b > 0 or cur_c > 0:
                if turn == 0:
                    card = a_deck[n - cur_a]
                    cur_a -= 1
                    if card == 0:
                        turn = 0
                    elif card == 1:
                        turn = 1
                    else:
                        turn = 2
                elif turn == 1:
                    card = b_deck[m-cur_b]
                    cur_b -= 1
                    if card == 0:
                        turn = 0
                    elif card == 1:
                        turn = 1
                    else:
                        turn = 2
                else:
                    card = c_deck[k-cur_c]
                    cur_c -= 1
                    if card == 0:
                        turn = 0
                    elif card == 1:
                        turn = 1
                    else:
                        turn = 2
            if turn == 0:
                ans += 1
print(ans)
