#include <stdio.h>
#include <stdlib.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    long long a, b, c;
    scanf("%lld %lld %lld", &a, &b, &c);
    long long total = a * b * c;
    long long ans = total;
    for (long long i = 1; i < a; i++) {
        ans = min(ans, abs(i * b * c - (a - i) * b * c));
    }
    for (long long i = 1; i < b; i++) {
        ans = min(ans, abs(a * i * c - a * (b - i) * c));
    }
    for (long long i = 1; i < c; i++) {
        ans = min(ans, abs(a * b * i - a * b * (c - i)));
    }
    for (long long i = 1; i < a; i++) {
        for (long long j = 1; j < b; j++) {
            ans = min(ans, abs(i * j * c - (a - i) * (b - j) * c));
            ans = min(ans, abs(i * j * c - (a - i) * b * (c - j)));
            ans = min(ans, abs(i * j * c - a * (b -j) * (c - i)));
        }
    }
    for (long long i = 1; i < a; i++) {
        for (long long j = 1; j < c; j++) {
            ans = min(ans, abs(i * b * j - (a - i) * b * (c - j)));
        }
    }
    for (long long i = 1; i < b; i++) {
        for (long long j = 1; j < c; j++) {
            ans = min(ans, abs(a * i * j - a * (b - i) * (c - j)));
        }
    }
    printf("%lld\n", ans);
    return 0;
}
