import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long a = sc.nextLong();
        long b = sc.nextLong();
        long c = sc.nextLong();
        long total = a * b * c;
        long ans = Long.MAX_VALUE;
        for (long i = 1; i < a; i++) {
            long red = i * b * c;
            long blue = total - red;
            ans = Math.min(ans, Math.abs(red - blue));
        }
        for (long i = 1; i < b; i++) {
            long red = a * i * c;
            long blue = total - red;
            ans = Math.min(ans, Math.abs(red - blue));
        }
        for (long i = 1; i < c; i++) {
            long red = a * b * i;
            long blue = total - red;
            ans = Math.min(ans, Math.abs(red - blue));
        }
        System.out.println(ans);
    }
}
