#include <stdio.h>
#include <limits.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    int N;
    long long x;
    scanf("%d %lld", &N, &x);
    long long a[N];
    long long sum = 0;
    long long max = 0;
    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
        sum += a[i];
        max = max > a[i] ? max : a[i];
    }
    long long ans = sum;
    for (int i = 0; i < N; i++) {
        long long cur = sum - a[i] + (long long)(N - 1) * x;
        long long temp = 0;
        for (int j = 0; j < N; j++) {
            if (j != i)
                temp += a[j];
        }
        long long cur2 = temp + (long long)(N-1) * x;
        ans = min(ans, cur);
        ans = min(ans, cur2);


    }


    long long cur = 0;
    for(int i = 0; i < N; ++i){
        cur += a[i];
    }


    ans = min(ans,cur);

