import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long x = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long min = Long.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            long sum = 0;
            long[] b = new long[n];
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    b[j] = a[j];
                }
            }
            long cur = 0;
            boolean[] has = new boolean[n];
            for (int j = 0; j < n; j++) {
                if (b[j] > 0) {
                    cur += b[j];
                    has[j] = true;
                }
            }
            long count = 0;
            for(boolean h : has){
                if(h) count++;
            }
            if(count == n) {
                min = Math.min(min,cur);
                continue;
            }

            long cost = 0;
            long cur_cost = cur;
            boolean[] cur_has = Arrays.copyOf(has,n);

            while(true){
                boolean all = true;
                for(boolean h : cur_has) if(!h) all = false;
                if(all) break;
                int min_idx = -1;
                long min_a = Long.MAX_VALUE;
                for(int k=0; k<n; k++){
                    if(!cur_has[k]){
                        if(a[k] < min_a){
                            min_a = a[k];
                            min_idx = k;
                        }
                    }
                }
                cur_cost += min_a;
                cur_has[min_idx] = true;
                cost += x;
            }
            min = Math.min(min, cur_cost + cost);


        }
        System.out.println(min);
    }
}
