#include <stdio.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char a[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            scanf(" %c", &a[i][j]);
        }
    }
    char red[H][W], blue[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            red[i][j] = '.';
            blue[i][j] = '.';
        }
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (a[i][j] == '#') {
                red[i][j] = '#';
                blue[i][j] = '#';
            }
        }
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%c", red[i][j]);
        }
        printf("\n");
    }
    printf("\n");
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%c", blue[i][j]);
        }
        printf("\n");
    }
    return 0;
}
