#include <iostream>
#include <vector>

using namespace std;

int main() {
    int h, w;
    cin >> h >> w;

    vector<string> a(h);
    for (int i = 0; i < h; ++i) {
        cin >> a[i];
    }

    vector<string> red(h, string(w, '.'));
    vector<string> blue(h, string(w, '.'));

    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (a[i][j] == '#') {
                red[i][j] = '#';
                blue[i][j] = '#';
            }
        }
    }

    for (int i = 0; i < h; ++i) {
        cout << red[i] << endl;
    }
    cout << endl;
    for (int i = 0; i < h; ++i) {
        cout << blue[i] << endl;
    }

    return 0;
}
