import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        char[][] a = new char[H][W];
        for (int i = 0; i < H; i++) {
            String s = sc.next();
            for (int j = 0; j < W; j++) {
                a[i][j] = s.charAt(j);
            }
        }
        char[][] red = new char[H][W];
        char[][] blue = new char[H][W];
        for (int i = 0; i < H; i++) {
            Arrays.fill(red[i], '.');
            Arrays.fill(blue[i], '.');
        }
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (a[i][j] == '#') {
                    red[i][j] = '#';
                    blue[i][j] = '#';
                }
            }
        }
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                System.out.print(red[i][j]);
            }
            System.out.println();
        }
        System.out.println();
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                System.out.print(blue[i][j]);
            }
            System.out.println();
        }
    }
}
