#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    a[i]--; 
  }

  long long count = 0;
  vector<long long> dist(n, -1);
  vector<long long> q;
  q.push_back(0);
  dist[0] = 0;

  int head = 0;
  while (head < q.size()) {
    long long u = q[head++];
    if (dist[u] == k) continue;
    if (a[u] != 0 && dist[a[u]] == -1) {
      dist[a[u]] = dist[u] + 1;
      q.push_back(a[u]);
    } else if (a[u] != 0 && dist[a[u]] > dist[u] +1) {
        dist[a[u]] = dist[u] + 1;
    }
  }

  for (int i = 0; i < n; ++i) {
    if (dist[i] == -1 || dist[i] != k) count++;
  }

  cout << count << endl;
  return 0;
}
