import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextLong();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int count = 0;
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            int cur = i + 1;
            int steps = 0;
            while (cur != 1 && steps < k) {
                cur = a[cur - 1];
                steps++;
            }
            if (steps == k)
                b[i] = 1;
            else
                count++;
        }
        System.out.println(count);
    }
}
