def solve():
    H, W = map(int, input().split())
    grid = [list(input()) for _ in range(H)]

    robots = []
    exit_r, exit_c = -1, -1
    for r in range(H):
        for c in range(W):
            if grid[r][c] == 'o':
                robots.append((r, c))
            elif grid[r][c] == 'E':
                exit_r, exit_c = r, c

    max_saved = 0
    for i in range(1 << (H + W + 1)):
        saved = 0
        curr_robots = robots[:]
        curr_grid = [row[:] for row in grid]

        moves = bin(i)[2:].zfill(H + W + 1)

        for move in moves:
            dr, dc = 0,0
            if move == '0':
                dr = -1
            elif move == '1':
                dr = 1
            elif move == '2':
                dc = -1
            elif move == '3':
                dc = 1


            next_robots = []
            for r, c in curr_robots:
                nr, nc = r + dr, c + dc
                if 0 <= nr < H and 0 <= nc < W:
                    if curr_grid[nr][nc] == 'E':
                        saved += 1
                    else:
                        next_robots.append((nr, nc))

            curr_robots = next_robots
        max_saved = max(max_saved, saved)

    print(len(robots) - max_saved)


