#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n, m;
int a[MAXN], b[MAXN];
int color[MAXN];
int visited[MAXN];
int graph[MAXN][MAXN];
int dist[MAXN];

int dfs(int u, int p, int c) {
    visited[u] = 1;
    color[u] = c;
    int cnt = 0;
    for (int v = 1; v <= n; v++) {
        if (graph[u][v] && v != p) {
            if (visited[v]) {
                if (color[u] == color[v]) return -1;
            } else {
                int res = dfs(v, u, 1 - c);
                if (res == -1) return -1;
                cnt += res;
            }
        }
    }
    return cnt + c;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a[i], &b[i]);
        graph[a[i]][b[i]] = graph[b[i]][a[i]] = 1;
    }
    int res = dfs(1, 0, 0);
    if (res == -1) {
        printf("-1\n");
    } else {
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            if (color[i] == 1) ans++;
        }
        printf("%d\n", ans);

    }
    return 0;
}
