#include <iostream>

int main() {
    long long N, K, X, Y;
    std::cin >> N >> K >> X >> Y;
    long long totalFee = 0;
    if (N <= K) {
        totalFee = N * X;
    } else {
        totalFee = K * X + (N - K) * Y;
    }
    std::cout << totalFee << std::endl;
    return 0;
}
