#include <stdio.h>
#include <string.h>

int main() {
    char w[101];
    scanf("%s", w);
    int count[26] = {0};
    for (int i = 0; i < strlen(w); i++) {
        count[w[i] - 'a']++;
    }
    int flag = 1;
    for (int i = 0; i < 26; i++) {
        if (count[i] % 2 != 0) {
            flag = 0;
            break;
        }
    }
    if (flag == 1) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
