#include <iostream>
#include <string>
#include <map>

using namespace std;

int main() {
  string w;
  cin >> w;

  map<char, int> counts;
  for (char c : w) {
    counts[c]++;
  }

  bool beautiful = true;
  for (char c = 'a'; c <= 'z'; ++c) {
    if (counts[c] % 2 != 0) {
      beautiful = false;
      break;
    }
  }

  if (beautiful) {
    cout << "Yes" << endl;
  } else {
    cout << "No" << endl;
  }

  return 0;
}
