import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String w = sc.next();
        int[] count = new int[26];
        for (char c : w.toCharArray()) {
            count[c - 'a']++;
        }
        boolean beautiful = true;
        for (int i = 0; i < 26; i++) {
            if (count[i] % 2 != 0) {
                beautiful = false;
                break;
            }
        }
        if (beautiful) {
            System.out.println("Yes");
        } else {
            System.out.println("No");
        }
    }
}
