#include <stdio.h>

int main() {
    int N, A;
    scanf("%d %d", &N, &A);
    int x[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &x[i]);
    }
    long long count = 0;
    for (int i = 0; i < (1 << N); i++) {
        int sum = 0;
        int num = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                sum += x[j];
                num++;
            }
        }
        if (num > 0 && sum == A * num) {
            count++;
        }
    }
    printf("%lld\n", count);
    return 0;
}
