#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, a;
  cin >> n >> a;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  long long count = 0;
  for (long long i = 0; i < (1LL << n); ++i) {
    long long sum = 0;
    long long num_cards = 0;
    for (long long j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        sum += x[j];
        num_cards++;
      }
    }
    if (num_cards > 0 && sum == a * num_cards) {
      count++;
    }
  }
  cout << count << endl;
  return 0;
}
