import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextInt();
        }
        long count = 0;
        for (int i = 0; i < (1 << n); i++) {
            long sum = 0;
            int num = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    sum += x[j];
                    num++;
                }
            }
            if (num > 0 && sum == (long) a * num) {
                count++;
            }
        }
        System.out.println(count);
    }
}
