#include <iostream>

long long f(long long b, long long n) {
    if (n < b) return n;
    return f(b, n / b) + n % b;
}

int main() {
    long long n, s;
    std::cin >> n >> s;
    for (long long b = 2; b <= 100000; ++b) {
        if (f(b, n) == s) {
            std::cout << b << std::endl;
            return 0;
        }
    }
    if (n == s && n >=2) {
        std::cout << n << std::endl;
        return 0;
    }
    std::cout << -1 << std::endl;
    return 0;
}
