import java.util.*;
import java.lang.*;

class Main {
    public static long f(long b, long n) {
        if (n < b) return n;
        return f(b, n / b) + n % b;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long s = sc.nextLong();
        long ans = -1;
        for (long b = 2; b <= Math.max(s, n) + 1; b++) {
            if (f(b, n) == s) {
                ans = b;
                break;
            }
        }
        System.out.println(ans);
    }
}
