n, a = map(int, input().split())
x = list(map(int, input().split()))

count = 0
for i in range(1, 1 << n):
    subset = []
    for j in range(n):
        if (i >> j) & 1:
            subset.append(x[j])
    if len(subset) > 0 and sum(subset) / len(subset) == a:
        count += 1

print(count)
