import java.util.Scanner;

public class Main {
    public static long f(long b, long n) {
        if (n < b) {
            return n;
        } else {
            return f(b, n / b) + n % b;
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long s = sc.nextLong();
        long ans = -1;
        for (long b = 2; b <= Math.min(n, 1000000); ++b) {
            if (f(b, n) == s) {
                ans = b;
                break;
            }
        }
        if(ans == -1 && n == s && s == 1) ans = n;
        System.out.println(ans);
    }
}
