#include <stdio.h>

int main() {
    int N, L, Q;
    scanf("%d", &N);
    long long x[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &x[i]);
    }
    scanf("%d", &L);
    scanf("%d", &Q);
    for (int i = 0; i < Q; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        a--;
        b--;
        int days = 0;
        int current = a;
        while (current != b) {
            int next = current;
            long long max_dist = L;
            while (next + 1 < N && x[next + 1] - x[current] <= max_dist) {
                next++;
            }
            if (next == b) {
                days++;
                break;
            }
            if (next == current) {

                days++;

                next++;

            } else {
                days++;
            }
            current = next;
        }
        printf("%d\n", days);
    }
    return 0;
}
