#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  long long l;
  cin >> l;

  int q;
  cin >> q;

  for (int i = 0; i < q; ++i) {
    int a, b;
    cin >> a >> b;
    a--; 
    b--;

    int days = 0;
    int current_hotel = a;
    while (current_hotel != b) {
      days++;
      long long max_reach = x[current_hotel] + l;
      int next_hotel = current_hotel + 1;
      while (next_hotel < n && x[next_hotel] <= max_reach) {
        next_hotel++;
      }
      current_hotel = next_hotel -1;
    }
    cout << days << endl;
  }
  return 0;
}
