import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] x = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
        }
        long l = sc.nextLong();
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt() - 1;
            int b = sc.nextInt() - 1;
            int days = 0;
            int curr = a;
            if (a < b) {
                while (curr < b) {
                    long maxDist = 0;
                    int next = curr + 1;
                    while (next <= b && x[next] - x[curr] <= l) {
                        maxDist = Math.max(maxDist, x[next] - x[curr]);
                        next++;
                    }
                    days++;
                    curr = next -1;
                }
            } else {
                while (curr > b) {
                    long maxDist = 0;
                    int next = curr - 1;
                    while (next >= b && x[curr] - x[next] <= l) {
                        maxDist = Math.max(maxDist, x[curr] - x[next]);
                        next--;
                    }
                    days++;
                    curr = next + 1;
                }
            }
            System.out.println(days);
        }
    }
}
