#include <iostream>
#include <string>
#include <vector>

using namespace std;

bool isGood(const string& s) {
  for (int k = 2; k <= s.length(); ++k) {
    for (int i = 0; i <= s.length() - k; ++i) {
      string y = s.substr(i, k);
      string concat = "";
      for (int j = 0; j < s.length() / k; ++j) {
        concat += y;
      }
      if (concat == s) return false;
    }
  }
  return true;
}

int main() {
  string w;
  cin >> w;

  int min_len = -1;
  long long count = 0;

  for (int i = 0; i < (1 << (w.length() - 1)); ++i) {
    vector<string> representation;
    string current_string = "";
    for (int j = 0; j < w.length(); ++j) {
      current_string += w[j];
      if ((i >> j) & 1 || j == w.length() - 1) {
        representation.push_back(current_string);
        current_string = "";
      }
    }

    bool good_representation = true;
    for (const string& s : representation) {
      if (!isGood(s)) {
        good_representation = false;
        break;
      }
    }

    if (good_representation) {
      if (min_len == -1 || representation.size() < min_len) {
        min_len = representation.size();
        count = 1;
      } else if (representation.size() == min_len) {
        count++;
      }
    }
  }

  cout << min_len << endl;
  cout << count % 1000000007 << endl;

  return 0;
}
