def is_good(s):
    n = len(s)
    for k in range(2, n + 1):
        for i in range(1, n // k + 1):
            if s == (s[:i] * k)[:n]:
                return False
    return True

def solve():
    w = input()
    n = len(w)

    min_len = float('inf')
    count = 0

    for i in range(1 << (n - 1)):
        representation = []
        current_string = ""
        for j in range(n):
            current_string += w[j]
            if (i >> j) & 1 or j == n - 1:
                representation.append(current_string)
                current_string = ""

        good = True
        for s in representation:
            if not is_good(s):
                good = False
                break

        if good:
            if len(representation) < min_len:
                min_len = len(representation)
                count = 1
            elif len(representation) == min_len:
                count += 1

    print(min_len)
    print(count % 1000000007)

solve()
