#include <stdio.h>
#include <string.h>

int main() {
    char S[1001];
    scanf("%s", S);
    int n = strlen(S);
    int north = 0, south = 0, east = 0, west = 0;
    for (int i = 0; i < n; i++) {
        if (S[i] == 'N') north++;
        else if (S[i] == 'S') south++;
        else if (S[i] == 'E') east++;
        else west++;
    }
    if (north == south && east == west) printf("Yes\n");
    else printf("No\n");
    return 0;
}
