#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;

  long long x = 0, y = 0;
  for (char c : s) {
    if (c == 'N') y++;
    else if (c == 'S') y--;
    else if (c == 'E') x++;
    else x--;
  }

  if (x == 0 && y == 0) cout << "Yes" << endl;
  else cout << "No" << endl;
  return 0;
}
