import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int x = 0, y = 0;
        for (int i = 0; i < n; i++) {
            char c = s.charAt(i);
            if (c == 'N') y++;
            else if (c == 'S') y--;
            else if (c == 'E') x++;
            else x--;
        }
        if (x == 0 && y == 0) System.out.println("Yes");
        else System.out.println("No");
    }
}
