#include <stdio.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int x[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &x[i]);
    }
    long long total_distance = 0;
    for (int i = 0; i < N; i++) {
        long long dist_a = 2 * x[i];
        long long dist_b = 2 * (K - x[i]);
        total_distance += (dist_a < dist_b) ? dist_a : dist_b;
    }
    printf("%lld\n", total_distance);
    return 0;
}
