#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int b[n];
    for (int i = 0; i < n; i++) {
        b[i] = a[i];
    }
    qsort(b, n, sizeof(int), [](const void *a, const void *b) {
        return (*(int *)a - *(int *)b);
    });
    int count = 0;
    int pos[n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (a[j] == b[i]) {
                pos[i] = j;
                break;
            }
        }
    }
    int cur = 0;
    for (int i = 0; i < n; i++) {
        if (pos[i] == cur) {
            cur++;
        } else {
            count += pos[i] - cur;
            cur = pos[i] + 1;
        }
    }
    printf("%d\n", count);
    return 0;
}
