#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> b = a;
  sort(b.begin(), b.end());

  vector<int> p(n);
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      if (a[i] == b[j]) {
        p[i] = j;
        break;
      }
    }
  }

  long long ans = 0;
  vector<bool> used(n, false);
  for (int i = 0; i < n; ++i) {
    if (used[i]) continue;
    if (p[i] == i) continue;
    int cur = i;
    while (p[cur] != i) {
      used[cur] = true;
      cur = p[cur];
    }
    used[cur] = true;
    ans++;
  }
  cout << ans << endl;
  return 0;
}
