import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Integer[] a = new Integer[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        Arrays.sort(a);
        int ans = 0;
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt();
        }
        int[] pos = new int[n];
        for (int i = 0; i < n; i++) {
            pos[b[i]] = i;
        }
        int[] c = new int[n];
        for (int i = 0; i < n; i++) {
            c[i] = pos[a[i]];
        }
        int len = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || c[i] > c[i - 1]) {
                len++;
            } else {
                ans += len - 1;
                len = 1;
            }
        }
        ans += len - 1;
        System.out.println(ans);
    }
}
