#include <stdio.h>
#include <stdlib.h>
#include <math.h>

long long cube_root(long long n) {
    long long lo = 0, hi = 1000000;
    long long ans = 0;
    while (lo <= hi) {
        long long mid = lo + (hi - lo) / 2;
        long long cube = mid * mid * mid;
        if (cube == n) {
            return mid;
        } else if (cube < n) {
            ans = mid;
            lo = mid + 1;
        } else {
            hi = mid - 1;
        }
    }
    return ans;
}

long long max_power(long long n) {
    long long count = 0;
    for (long long i = 2; i * i * i <= n; ++i) {
        while (n % i == 0) {
            n /= i;
            count++;
        }
    }
    if (n > 1) {
        count++;
    }
    return count;
}


int main() {
    int n;
    scanf("%d", &n);
    long long s[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &s[i]);
    }

    long long ans = 0;
    for (int i = 0; i < (1 << n); ++i) {
        long long count = 0;
        long long selected[n];
        int k=0;
        for (int j = 0; j < n; ++j) {
            if ((i >> j) & 1) {
                selected[k++] = s[j];
                count++;
            }
        }
        int flag = 1;
        for (int p = 0; p < k; ++p) {
            for (int q = p + 1; q < k; ++q) {
                long long prod = selected[p] * selected[q];
                if (cube_root(prod) * cube_root(prod) * cube_root(prod) == prod) {
                    flag = 0;
                    break;
                }
            }
            if(flag == 0) break;
        }
        if (flag) {
            ans = ans > count ? ans : count;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
