#include <iostream>
#include <vector>
#include <cmath>
#include <map>

long long cube_free(long long n) {
    long long res = 1;
    for (long long i = 2; i * i * i <= n; ++i) {
        long long cnt = 0;
        while (n % i == 0) {
            n /= i;
            cnt++;
        }
        if (cnt % 3 != 0) res *= i;
    }
    if (n > 1) res *= n;
    return res;
}

int main() {
    int n;
    std::cin >> n;
    std::map<long long, int> counts;
    std::vector<long long> s(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> s[i];
        long long cf = cube_free(s[i]);
        counts[cf]++;
    }
    int max_count = 0;
    for (auto const& [key, val] : counts) {
        max_count = std::max(max_count, val);
    }
    std::cout << max_count << std::endl;
    return 0;
}
