#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  long long min_dist = -1;

  for (int i = 0; i < (1 << n); ++i) {
    long long current_dist = 0;
    vector<bool> used(n, false);

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) { // Use type A robot
        current_dist += 2 * x[j];
        used[j] = true;
      }
    }

    for (int j = 0; j < n; ++j) {
      if (!used[j]) { // Use type B robot
        current_dist += 2 * (k - x[j]);
      }
    }

    if (min_dist == -1 || current_dist < min_dist) {
      min_dist = current_dist;
    }
  }

  cout << min_dist << endl;
  return 0;
}
