import math

def is_cubic(n):
    cbrt = round(n**(1/3))
    return cbrt**3 == n

def max_circled(nums):
    groups = {}
    for num in nums:
        x = num
        i = 2
        factors = {}
        while i * i <= x:
            count = 0
            while x % i == 0:
                x //= i
                count += 1
            if count > 0:
                factors[i] = count
            i += 1
        if x > 1:
            factors[x] = 1

        key = tuple(sorted(factors.items()))
        if key not in groups:
            groups[key] = 0
        groups[key] +=1

    ans = 0
    for val in groups.values():
        ans += val //2 *2 + min(1, val%2)

    return ans


N = int(input())
nums = []
for _ in range(N):
    nums.append(int(input()))

print(max_circled(nums))
