#include <stdio.h>

int main() {
    long long n, q;
    scanf("%lld %lld", &n, &q);
    long long arr[n];
    for (long long i = 0; i < n; i++) {
        arr[i] = i + 1;
    }
    long long qi;
    for (long long i = 0; i < q; i++) {
        scanf("%lld", &qi);
        long long newArr[qi];
        long long k = 0;
        while (k < qi) {
            for (long long j = 0; j < n && k < qi; j++) {
                newArr[k++] = arr[j];
            }
        }
        for (long long j = 0; j < qi; j++) {
            arr[j] = newArr[j];
        }
        n = qi;
    }
    long long count[n];
    for (long long i = 0; i < n; i++) {
        count[i] = 0;
    }
    for (long long i = 0; i < n; i++) {
        count[arr[i] -1]++;
    }
    for (long long i = 0; i < n; i++) {
        printf("%lld\n", count[i]);
    }
    return 0;
}
