#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, q;
  cin >> n >> q;

  vector<long long> a(n);
  for (long long i = 0; i < n; ++i) {
    a[i] = i + 1;
  }

  for (int i = 0; i < q; ++i) {
    long long qi;
    cin >> qi;
    vector<long long> b;
    long long k = qi;
    while (k > 0) {
      for (long long j = 0; j < n && k > 0; ++j) {
        b.push_back(a[j]);
        k--;
      }
    }
    a = b;
  }

  vector<long long> counts(n + 1, 0);
  for (long long x : a) {
    counts[x]++;
  }

  for (long long i = 1; i <= n; ++i) {
    cout << counts[i] << endl;
  }

  return 0;
}
