import java.util.*;
public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long q = sc.nextLong();
        long[] a = new long[(int)n];
        for(int i=0;i<n;i++) a[i] = i+1;
        for(int i=0;i<q;i++){
            long qi = sc.nextLong();
            long[] b = new long[(int)qi];
            long idx = 0;
            while(idx < qi){
                for(int j=0;j<n;j++){
                    if(idx == qi) break;
                    b[(int)idx] = a[j];
                    idx++;
                }
            }
            a = b;
            n = qi;
        }
        long[] ans = new long[(int)n];
        for(long x : a) ans[(int)(x-1)]++;
        for(int i=0;i<ans.length;i++){
            long cnt = 0;
            for(int j=0;j<a.length;j++){
                if(a[j] == i+1) cnt++;
            }
            System.out.println(cnt);
        }
    }
}
