#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    long long H, W, K;
    scanf("%lld %lld %lld", &H, &W, &K);
    char s[H][W + 1];
    for (long long i = 0; i < H; i++) {
        scanf("%s", s[i]);
    }
    if (K == 0) {
        printf("1\n");
        return 0;
    }
    long long count = 0;
    long long size = 1;
    for (long long i = 0; i < H; i++) {
        for (long long j = 0; j < W; j++) {
            if (s[i][j] == '#')
                count++;
        }
    }
    long long ans = 1;
    long long cur_size = 1;
    for (long long i = 0; i < K; i++) {
        long long next_size = cur_size * H;
        long long next_count = 0;
        for (long long r = 0; r < H; r++) {
            for (long long c = 0; c < W; c++) {
                if (s[r][c] == '#')
                    next_count = (next_count + count) % MOD;
            }
        }
        ans = next_count;
        cur_size = next_size;

    }
    printf("%lld\n", ans);
    return 0;
}
