H, W, K = map(int, input().split())
grid = [input() for _ in range(H)]
mod = 10**9 + 7

def solve():
    if K == 0:
        return 1

    size = 1
    for _ in range(K):
        size *= max(H,W)


    count = 0
    for r in range(H):
        for c in range(W):
            if grid[r][c] == '#':
                count += 1


    ans = pow(count, K, mod)
    return ans

print(solve())
