#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long min_cost = -1;
    for (int i = -100; i <= 100; i++) {
        long long current_cost = 0;
        for (int j = 0; j < n; j++) {
            current_cost += (long long)(a[j] - i) * (a[j] - i);
        }
        if (min_cost == -1 || current_cost < min_cost) {
            min_cost = current_cost;
        }
    }
    printf("%lld\n", min_cost);
    return 0;
}
