#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>

using namespace std;

long long cost(const vector<long long>& a, long long target) {
  long long res = 0;
  for (long long x : a) {
    res += (x - target) * (x - target);
  }
  return res;
}

int main() {
  int n;
  cin >> n;
  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_cost = -1;
  for (long long i = -100; i <= 100; ++i) {
    long long cur_cost = cost(a, i);
    if (min_cost == -1 || cur_cost < min_cost) {
      min_cost = cur_cost;
    }
  }
  cout << min_cost << endl;
  return 0;
}
