s = input()
n = len(s)
for i in range(n - 1):
    for j in range(i + 1, n):
        sub = s[i:j + 1]
        length = len(sub)
        counts = {}
        for char in sub:
            counts[char] = counts.get(char, 0) + 1
        max_count = 0
        for count in counts.values():
            max_count = max(max_count, count)
        if max_count > length / 2:
            print(i + 1, j + 1)
            exit()
print(-1, -1)

