#include <iostream>
#include <vector>

using namespace std;

long long power(long long base, long long exp, long long mod) {
  long long res = 1;
  base %= mod;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % mod;
    base = (base * base) % mod;
    exp /= 2;
  }
  return res;
}

int main() {
  int n, c;
  cin >> n >> c;

  vector<long long> a(n), b(n);
  for (int i = 0; i < n; ++i) cin >> a[i];
  for (int i = 0; i < n; ++i) cin >> b[i];

  long long mod = 1e9 + 7;
  long long ans = 0;

  vector<long long> candies(n, 0);
  function<void(int)> distribute = [&](int k) {
    if (k == n) {
      long long activity = 1;
      for (int i = 0; i < n; ++i) {
          long long x = 0;
          for(long long j=a[i]; j<=b[i]; ++j){
              x += power(j,candies[i],mod);
              x%=mod;
          }
        activity = (activity * x) % mod;
      }
      ans = (ans + activity) % mod;
      return;
    }
    for (long long i = 0; i <= c; ++i) {
      if (accumulate(candies.begin(), candies.end(), 0LL) + i <= c) {
        candies[k] = i;
        distribute(k + 1);
        candies[k] = 0;
      }
    }
  };

  distribute(0);
  cout << ans << endl;
  return 0;
}
