#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int main() {
    int n;
    char s[5001];
    scanf("%d", &n);
    scanf("%s", s);
    int len = strlen(s);
    long long dp[n + 1][n + 1];
    memset(dp, 0, sizeof(dp));
    dp[0][0] = 1;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= i; j++) {
            if (dp[i][j] == 0) continue;
            if (i + 1 <= n) {
                dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % MOD;
            }
            if (j > 0 && i + 1 <= n) {
                dp[i + 1][j - 1] = (dp[i + 1][j - 1] + dp[i][j] * 2) % MOD;
            }

        }
    }
    long long ans = 0;
    if (len > 0)
        ans = dp[n][len];
    printf("%lld\n", ans);
    return 0;
}
